use think\Model;

class UserModel extends Model {

    protected $table = 'user';

    // 新增
    public static function insertUser() {
        $user = new UserModel();
        $user->allowField(true)->save(input(""));
        return $user->id;
    }

    // 删除(物理删除)
    public static function deleteUser() {
        $user = new UserModel();
        $user::destroy(input("id"));
    }

    // 更新（支持逻辑删除）
    public static function updateUser() {
        $user = new UserModel();
        $user->allowField(true)->save(input(""), ["id"=>input("id")]);
    }

    // 获取列表(分页)
    public static function getUserList() {
        $user = new UserModel();
        $where = [];
        $status = input("status");
        if ($status != null) {
            $where['status'] = $status;
        }
        // 支持扩展
        return $user->where($where)->paginate(input("size"), false, ['page' => input("page")]);
    }

    // 获取详情
    public static function getUserDetail() {
        $user = new UserModel();
        $where = [];
        $where['id'] = input("id");
        // 支持扩展
        return $user->where($where)->find();
    }
}
