    import request from '@/utils/request'

    // 获取SystemDept列表(分页)数据
    export function getSystemDeptList(query) {
        return request({
            url: '/api/SystemDept/getSystemDeptList',
            method: 'get',
            params: query
        })
    }

    // 获取SystemDept详情数据
    export function getSystemDeptDetail(id) {
        return request({
            url: '/api/SystemDept/getSystemDeptDetail?id=' + id,
            method: 'get'
        })
    }

    // 新增SystemDept数据
    export function addSystemDept(data) {
        return request({
            url: '/api/SystemDept/addSystemDept',
            method: 'post',
            data: data
        })
    }

    // 更新SystemDept数据
    export function updateSystemDept(data) {
        return request({
            url: '/api/SystemDept/updateSystemDept',
            method: 'put',
            data: data
        })
    }

    // 删除SystemDept数据
    export function deleteSystemDept(id) {
        return request({
            url: '/api/SystemDept/deleteSystemDept?id='+id,
            method: 'delete'
        })
    }
